console.log("Dashboard rendering...", { authenticated, chartData });
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar, Line, Doughnut } from 'react-chartjs-2';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const [selectedChart, setSelectedChart] = useState('emails');
  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [authenticated, setAuthenticated] = useState(false);
  const [authMessage, setAuthMessage] = useState('');

  // Check authentication status on component mount
  useEffect(() => {
    checkAuthStatus();

    // Check for auth success in URL params
    const urlParams = new URLSearchParams(window.location.search);
    if (urlParams.get('auth') === 'success') {
      setAuthMessage('Authentication successful! You can now view your email analytics.');
      setAuthenticated(true);
      // Clean up URL
      window.history.replaceState({}, document.title, window.location.pathname);
    }
  }, []);

  // Fetch chart data when selection changes
  useEffect(() => {
    if (authenticated) {
      fetchChartData();
    }
  }, [selectedChart, authenticated]);

  const checkAuthStatus = async () => {
    try {
      const response = await axios.get('/api/auth/status');
      setAuthenticated(response.data.authenticated);
    } catch (error) {
      console.error('Error checking auth status:', error);
      setAuthenticated(false);
    }
  };

  const fetchChartData = async () => {
    setLoading(true);
    setError(null);

    try {
      let endpoint = '';
      switch (selectedChart) {
        case 'emails':
          endpoint = '/api/emails/summary';
          break;
        case 'companies':
          endpoint = '/api/companies/weekly';
          break;
        case 'stages':
          endpoint = '/api/companies/stages';
          break;
        default:
          endpoint = '/api/emails/summary';
      }

      const response = await axios.get(endpoint);
      setChartData(response.data);
    } catch (error) {
      console.error('Error fetching chart data:', error);
      setError('Failed to fetch chart data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleAuthentication = () => {
    window.location.href = 'http://localhost:5000/auth';
  };

  const getChartConfig = () => {
    if (!chartData || !chartData.data) return null;

    const colors = [
      '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF',
      '#FF9F40', '#FF6384', '#C9CBCF', '#4BC0C0', '#FF6384'
    ];

    switch (selectedChart) {
      case 'emails':
        return {
          type: 'bar',
          data: {
            labels: chartData.data.map(item => item.name),
            datasets: [{
              label: 'Outbound Emails Sent',
              data: chartData.data.map(item => item.emails),
              backgroundColor: colors.slice(0, chartData.data.length),
              borderColor: colors.slice(0, chartData.data.length),
              borderWidth: 1
            }]
          },
          options: {
            responsive: true,
            plugins: {
              legend: {
                position: 'top',
              },
              title: {
                display: true,
                text: 'Total Outbound Emails Sent per Team Member'
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                ticks: {
                  stepSize: 1
                }
              }
            }
          }
        };

      case 'companies':
        return {
          type: 'line',
          data: {
            labels: chartData.data.map(item => {
              const date = new Date(item.week);
              return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
            }),
            datasets: [{
              label: 'Companies Sourced',
              data: chartData.data.map(item => item.companies),
              borderColor: '#36A2EB',
              backgroundColor: 'rgba(54, 162, 235, 0.1)',
              fill: true,
              tension: 0.4
            }]
          },
          options: {
            responsive: true,
            plugins: {
              legend: {
                position: 'top',
              },
              title: {
                display: true,
                text: 'Companies Sourced per Week'
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                ticks: {
                  stepSize: 1
                }
              }
            }
          }
        };

      case 'stages':
        return {
          type: 'doughnut',
          data: {
            labels: chartData.data.map(item => item.stage),
            datasets: [{
              label: 'Companies by Stage',
              data: chartData.data.map(item => item.companies),
              backgroundColor: colors.slice(0, chartData.data.length),
              borderColor: '#fff',
              borderWidth: 2
            }]
          },
          options: {
            responsive: true,
            plugins: {
              legend: {
                position: 'right',
              },
              title: {
                display: true,
                text: 'Number of Companies in Each Stage'
              }
            }
          }
        };

      default:
        return null;
    }
  };

  const renderChart = () => {
    const config = getChartConfig();
    if (!config) return null;

    switch (config.type) {
      case 'bar':
        return <Bar data={config.data} options={config.options} />;
      case 'line':
        return <Line data={config.data} options={config.options} />;
      case 'doughnut':
        return <Doughnut data={config.data} options={config.options} />;
      default:
        return null;
    }
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Gmail Analytics Dashboard</h1>
        <p>Track email performance and company sourcing metrics</p>
      </div>

      {authMessage && (
        <div className="success-message">
          {authMessage}
        </div>
      )}

      {!authenticated ? (
        <div className="auth-section">
          <h2>Authentication Required</h2>
          <p>Please authenticate with Google to access your Gmail analytics.</p>
          <button className="auth-button" onClick={handleAuthentication}>
            ðŸ”— Authenticate with Google
          </button>
        </div>
      ) : (
        <>
          <div className="controls">
            <label htmlFor="chart-select">Select Chart Type:</label>
            <select
              id="chart-select"
              value={selectedChart}
              onChange={(e) => setSelectedChart(e.target.value)}
            >
              <option value="emails">Total Outbound Emails per Team Member</option>
              <option value="companies">Companies Sourced per Week</option>
              <option value="stages">Companies by Stage</option>
            </select>
          </div>

          {error && (
            <div className="error">
              {error}
            </div>
          )}

          <div className="chart-container">
            {loading ? (
              <div className="loading">
                <p>Loading chart data...</p>
              </div>
            ) : (
              renderChart()
            )}
          </div>

          {chartData && !chartData.success && (
            <div className="error">
              Note: Using mock data due to API limitations. Authenticate and ensure proper Gmail API access for real data.
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Dashboard;
